typeOptions.action:
var redirectUri = config.WEBHOOK_URL + '/authCallback'; 
var url = 'https://api' + (config.ENDPOINT_CONFIG['environment'] === 'dev' ? '.dev' : config.ENDPOINT_CONFIG['environment'] === 'stage' ? '.stage' : '') + (config.ENDPOINT_CONFIG['region'] === 'us-1' ? '.us-1' : config.ENDPOINT_CONFIG['region'] === 'ca-1' ? '.ca-1' : config.ENDPOINT_CONFIG['region'] === 'uk-1' ? '.uk-1' : config.ENDPOINT_CONFIG['region']) + '.veritone.com/v1/admin/oauth/authorize?response_type=code&client_id=' + config.ENDPOINT_CONFIG['clientId'] + '&redirect_uri=' + redirectUri + '&scope=all';
var win = window.open(url, 'Authorization page', 'toolbar=no,scrollbars=no,location=no,statusbar=no,menubar=no,resizable=0,width=500,height=600,left='+((screen.width/2)-250)+',top='+((screen.height/2)-250)+',');

var self = this;

var intervalFn = function() {
	try {
		if (!win || !win.document) {
			window.clearInterval(pollTimer);
		}else{
			if (win.document.URL.indexOf('/authCallback') !== -1) {
				win.innerWidth = 100;
				win.innerHeight = 100;
				win.screenX = screen.width;
				win.screenY = screen.height;
				window.clearInterval(pollTimer);
				url = win.document.URL;
				var pos = url.indexOf('code=');
				if(pos > -1) {
					var token = url.substring(pos + 5);
										var limit = token.indexOf("&");
					if (limit > -1) {
						token = token.substring(0, limit);
					}
config.code = token;
					config.redirectUri = redirectUri;
					if (utils.connectToService) {
						utils.connectToService();
					}
				}
				try{win.close();}catch(e){}
			}
		}
	} catch(e) {}
};

var pollTimer = window.setInterval(function() {intervalFn.apply(self);}, 500);
